# Originally wrote fast in python then worked it out in go after

line = ""

windowSize = 4
for i, c in enumerate(line):
	if len(set(line[i:i+windowSize])) == windowSize:
		print(i+4)
		break

windowSize = 14
for i, c in enumerate(line):
	if len(set(line[i:i+windowSize])) == windowSize:
		print(i+14)
		break